//Swapping Two numbers
package com.globalogic;

public class swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=4,b=5,c;
		System.out.println("before printing "+a+" and "+b);
		c=a;
		a=b;
		b=c;
		System.out.println("after printing "+a+" and "+b);

	}

}
